
public class exemploArray1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Criar vetor
		int[] numeros = new int[3];
		//inicializar valores
		numeros[0] = 1;
		numeros[1] = 2;
		numeros[2] = 3;
		//Exibe valores
		for(int i = 0; i < numeros.length; i++){
			System.out.println(numeros[i]);
		}
	}

}
